const main=document.querySelector(".main__inner"),burgerBtn=document.querySelector(".nav__btn"),menu=document.querySelector(".nav__list");function toggleMenu(){main.classList.toggle("active"),menu.classList.toggle("active"),burgerBtn.classList.toggle("pushed")}burgerBtn.addEventListener("click",e=>{e.preventDefault(),toggleMenu()});const langItemArr=document.querySelectorAll(".lang__item"),langBtn=document.querySelector(".lang__btn");function openLang(){for(let e=1;e<langItemArr.length;e++)langItemArr[e].classList.toggle("lang__item--hidden");langBtn.classList.toggle("lang__btn--active")}langBtn.addEventListener("click",e=>{e.preventDefault(),openLang()});const card=document.querySelector(".card"),moreInfo=document.querySelector(".more-info"),cardBtn=document.querySelector(".card__btn");function openCardText(){main.classList.toggle("active"),card.classList.toggle("active"),moreInfo.classList.toggle("active"),cardBtn.classList.toggle("active")}cardBtn.addEventListener("click",e=>{e.preventDefault(),openCardText()});const swiper=new Swiper(".swiper-container",{direction:"horizontal",loop:!0,speed:400,pagination:{el:".swiper-pagination",dynamicBullets:!0},autoplay:{delay:2e3}});